import React, { useState, useEffect, useRef } from 'react';
import useImage from '../../hooks/useImage'

const ImageEmployee = () => {
  const [searchInput, setSearchQuery] = useState('');
  const [imageBlob, setImageBlob] = useState(null);
  const [imageName, setImageName] = useState('');
  const [tool, setTool] = useState('Pen'); // Default tool is 'Pen'
  const [color, setColor] = useState('#000000'); // Default color is 'black'
  const [size, setSize] = useState('Medium'); // Default size is 'Medium'

  // Refs for the canvas and its 2D context
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const isDrawing = useRef(false);

  useEffect(() => {
    // Initialize canvas and 2D context
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    contextRef.current = context;

    if (imageBlob) {
      // Draw the image on the canvas
      const img = new Image();
      img.onload = () => {
        canvas.width = img.width;
        canvas.height = img.height;
        contextRef.current.drawImage(img, 0, 0, img.width, img.height);
      };
      img.src = URL.createObjectURL(imageBlob);
    }
  }, [imageBlob]);

  const searchImage = () => {
    // Perform the search logic here
    console.log('Searching for image:', searchInput);

    fetch(`http://localhost:3001/image/${searchInput}`)
      .then((response) => {
        if (response.ok) {
          setImageName(searchInput); // Save the image name
          return response.blob();
        } else {
          throw new Error('Image not found');
        }
      })
      .then((blob) => {
        setImageBlob(blob);
      })
      .catch((error) => {
        console.error('Something went wrong while fetching the image:', error);
      });
  };

  // Handle painting on the canvas
  const startDrawing = (e) => {
    isDrawing.current = true;
    draw(e);
  };

  const stopDrawing = () => {
    isDrawing.current = false;
    contextRef.current.beginPath();
  };

  const draw = (e) => {
    if (!isDrawing.current) return;

    const x = e.nativeEvent.offsetX;
    const y = e.nativeEvent.offsetY;

    contextRef.current.fillStyle = color;
    contextRef.current.strokeStyle = color;
    contextRef.current.lineWidth = getSizeValue();

    if (tool === 'Pen') {
      contextRef.current.lineTo(x, y);
      contextRef.current.stroke();
      contextRef.current.beginPath();
      contextRef.current.arc(x, y, getSizeValue() / 2, 0, Math.PI * 2);
      contextRef.current.fill();
      contextRef.current.beginPath();
      contextRef.current.moveTo(x, y);
    } else {
      const text = prompt('Enter text:');
      contextRef.current.font = `bold ${getSizeValue() * 5}px Arial`;
      contextRef.current.fillText(text, x, y);
    }
  };

  const getSizeValue = () => {
    switch (size) {
      case 'Small':
        return 3;
      case 'Medium':
        return 6;
      case 'Large':
        return 9;
      default:
        return 6;
    }
  };

  const saveImage = () => {
    // Convert the canvas content to a blob
    canvasRef.current.toBlob((blob) => {
      if (blob) {
        // Create a FormData object to send the image file
        const formData = new FormData();
        formData.append('image', blob, `${imageName}.png`);

        // Perform the POST request to upload the image
        fetch('http://localhost:3001/upload', {
          method: 'POST',
          body: formData,
        })
          .then((response) => response.json())
          .then((data) => {
            console.log('Image uploaded successfully:', data);
          })
          .catch((error) => {
            console.error('Error uploading image:', error);
          });
      }
    });
  };

  return (
    <div>
      <h1>Bildhantering</h1>
      <input
        type="text"
        placeholder="Sök efter bild"
        value={searchInput}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={searchImage}>Search</button>
      <br />
      <div>
        <canvas
          ref={canvasRef}
          onMouseDown={startDrawing}
          onMouseUp={stopDrawing}
          onMouseOut={stopDrawing}
          onMouseMove={draw}
        ></canvas>
        <label>
          Tool:
          <select value={tool} onChange={(e) => setTool(e.target.value)}>
            <option value="Pen">Pen</option>
            <option value="Text">Text</option>
          </select>
        </label>
        <label>
          Color:
          <input type="color" value={color} onChange={(e) => setColor(e.target.value)} />
        </label>
        <label>
          Size:
          <select value={size} onChange={(e) => setSize(e.target.value)}>
            <option value="Small">Small</option>
            <option value="Medium">Medium</option>
            <option value="Large">Large</option>
          </select>
        </label>
        <button onClick={saveImage}>Save</button>
      </div>
    </div>
  );
};

export default ImageEmployee;
