import React, { useState, useEffect, useRef } from 'react';

const ImageEmployee = () => {
  const [searchInput, setSearchQuery] = useState('');
  const [imageBlob, setImageBlob] = useState(null);

  // Refs for the canvas and its 2D context
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  useEffect(() => {
    // Initialize canvas and 2D context
    const canvas = canvasRef.current;
    canvas.width = 500; // Set your desired width
    canvas.height = 500; // Set your desired height
    const context = canvas.getContext('2d');
    contextRef.current = context;
  }, []);

  const searchImage = () => {
    // Perform the search logic here
    console.log('Searching for image:', searchInput);

    fetch(`http://localhost:3001/image/${searchInput}`)
      .then((response) => {
        if (response.ok) {
          return response.blob();
        } else {
          throw new Error('Image not found');
        }
      })
      .then((blob) => {
        setImageBlob(blob);

        // Draw the image on the canvas
        const img = new Image();
        img.onload = () => {
          contextRef.current.drawImage(img, 0, 0, canvasRef.current.width, canvasRef.current.height);
        };
        img.src = URL.createObjectURL(blob);
      })
      .catch((error) => {
        console.error('Something went wrong while fetching the image:', error);
      });
  };

  // Handle painting on the canvas
  const handleCanvasClick = (e) => {
    const x = e.nativeEvent.offsetX;
    const y = e.nativeEvent.offsetY;

    // Customize your painting logic here
    contextRef.current.fillStyle = 'red';
    contextRef.current.fillRect(x, y, 10, 10);
  };

  return (
    <div>
      <h1>Bildhantering</h1>
      <input
        type="text"
        placeholder="SÃ¶k efter bild"
        value={searchInput}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={searchImage}>Search</button>
      <canvas ref={canvasRef} onClick={handleCanvasClick}></canvas>
    </div>
  );
};

export default ImageEmployee;
