import React, { useState, useEffect, useRef } from 'react';

const ImageEmployee = () => {
  const [searchInput, setSearchQuery] = useState('');
  const [imageBlob, setImageBlob] = useState(null);
  const [tool, setTool] = useState('Pen'); // Default tool is 'Pen'
  const [color, setColor] = useState('#FF0000'); // Default color is 'red'
  const [size, setSize] = useState('Medium'); // Default size is 'Medium'

  // Refs for the canvas and its 2D context
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  useEffect(() => {
    // Initialize canvas and 2D context
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    contextRef.current = context;

    if (imageBlob) {
      // Draw the image on the canvas
      const img = new Image();
      img.onload = () => {
        canvas.width = img.width;
        canvas.height = img.height;
        contextRef.current.drawImage(img, 0, 0, img.width, img.height);
      };
      img.src = URL.createObjectURL(imageBlob);
    }
  }, [imageBlob]);

  const searchImage = () => {
    // Perform the search logic here
    console.log('Searching for image:', searchInput);

    fetch(`http://localhost:3001/image/${searchInput}`)
      .then((response) => {
        if (response.ok) {
          return response.blob();
        } else {
          throw new Error('Image not found');
        }
      })
      .then((blob) => {
        setImageBlob(blob);
      })
      .catch((error) => {
        console.error('Something went wrong while fetching the image:', error);
      });
  };

  // Handle painting on the canvas
  const handleCanvasClick = (e) => {
    const x = e.nativeEvent.offsetX;
    const y = e.nativeEvent.offsetY;

    // Customize your painting logic based on the selected tool, color, and size
    if (tool === 'Pen') {
      contextRef.current.fillStyle = color;
      contextRef.current.fillRect(x, y, getSizeValue(), getSizeValue());
    } else if (tool === 'Text') {
      const text = prompt('Enter text:');
      contextRef.current.fillStyle = color;
      contextRef.current.font = `${getSizeValue() * 5}px Arial`;
      contextRef.current.fillText(text, x, y);
    }
  };

  // Helper function to get size value based on the selected size
  const getSizeValue = () => {
    switch (size) {
      case 'Small':
        return 5;
      case 'Medium':
        return 10;
      case 'Large':
        return 15;
      default:
        return 10;
    }
  };

  return (
    <div>
      <h1>Bildhantering</h1>
      <div>
        <label>
          Tool:
          <select value={tool} onChange={(e) => setTool(e.target.value)}>
            <option value="Pen">Pen</option>
            <option value="Text">Text</option>
          </select>
        </label>
        <label>
          Color:
          <input type="color" value={color} onChange={(e) => setColor(e.target.value)} />
        </label>
        <label>
          Size:
          <select value={size} onChange={(e) => setSize(e.target.value)}>
            <option value="Small">Small</option>
            <option value="Medium">Medium</option>
            <option value="Large">Large</option>
          </select>
        </label>
      </div>
      <input
        type="text"
        placeholder="SÃ¶k efter bild"
        value={searchInput}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={searchImage}>Search</button>
      <br />
      <canvas ref={canvasRef} onClick={handleCanvasClick}></canvas>
    </div>
  );
};

export default ImageEmployee;
