import React, { useState, useRef } from 'react';
import useImage from '../../hooks/useImage';

const ImagePatient = () => {
  const [searchInput, setSearchQuery] = useState('');

  // Refs for the canvas and its 2D context
  const canvasRef = useRef(null);

  const { imageBlob, searchImage } = useImage(canvasRef);

  // Initialize canvas and 2D context
  const initializeCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    if (imageBlob) {
      // Draw the image on the canvas
      const img = new Image();
      img.onload = () => {
        canvas.width = img.width;
        canvas.height = img.height;
        context.drawImage(img, 0, 0, img.width, img.height);
      };
      img.src = URL.createObjectURL(imageBlob);
    }
  };

  const handleSearchImage = () => {
    searchImage(searchInput);
  };

  return (
    <div>
      <h1>Bildhantering</h1>
      <input
        type="text"
        placeholder="SÃ¶k efter bild"
        value={searchInput}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={handleSearchImage}>Search</button>
      <br />
      <div>
        <canvas ref={canvasRef} onLoad={initializeCanvas}></canvas>
      </div>
    </div>
  );
};

export default ImagePatient;
