import React, { useState, useRef, useEffect } from 'react';

const ImageEmployee = () => {
  const [canvases, setCanvases] = useState({});
  const fileInputRef = useRef(null);

  const openFileInput = () => {
    fileInputRef.current.click();
  };

  const handleFileUpload = (event) => {
    const fileInput = event.target;
    const file = fileInput.files[0];

    if (file) {
      const formData = new FormData();
      formData.append('image', file);

      fetch('http://localhost:3001/upload', {
        method: 'POST',
        body: formData,
      })
        .catch((error) => {
          console.error(error);
        });
    }
  };

  const searchImage = (frameNumber) => {
    const searchInput = document.getElementById(`searchInput${frameNumber}`).value;

    fetch(`http://localhost:3001/image/${searchInput}`)
      .then((response) => {
        if (response.ok) {
          return response.blob();
        } else {
          throw new Error('Image not found');
        }
      })
      .then((blob) => {
        const canvasContainer = document.querySelector(`#imageCanvas${frameNumber}`).parentElement;
        setupCanvas(canvasContainer, blob, frameNumber);
      })
      .catch((error) => {
        console.error('Something went wrong while fetching the image:', error);
      });
  };

  const setupCanvas = (container, imageBlob, frameNumber) => {
    const canvasRef = useRef(null);
    const toolSelectorRef = useRef(null);
    const colorPickerRef = useRef(null);
    const sizeSelectorRef = useRef(null);

    useEffect(() => {
      const canvas = canvasRef.current;
      const toolSelector = toolSelectorRef.current;
      const colorPicker = colorPickerRef.current;
      const sizeSelector = sizeSelectorRef.current;

      if (!canvas || !toolSelector || !colorPicker || !sizeSelector) {
        console.error(`Failed to get refs for frame ${frameNumber}`);
        return;
      }

      const context = canvas.getContext('2d');

      if (!context) {
        console.error(`Failed to get 2D context for canvas #imageCanvas${frameNumber}`);
        return;
      }

      const updatedCanvases = {
        ...canvases,
        [frameNumber]: {
          canvas,
          context,
          tool: 'pen',
          drawing: false,
          color: '#000000',
          size: 'small',
        },
      };

      const imageUrl = URL.createObjectURL(imageBlob);
      const image = new Image();
      image.src = imageUrl;
      image.onload = () => {
        canvas.width = image.width;
        canvas.height = image.height;

        updatedCanvases[frameNumber].context.clearRect(0, 0, canvas.width, canvas.height);
        updatedCanvases[frameNumber].context.drawImage(image, 0, 0, canvas.width, canvas.height);
      };

      canvas.addEventListener('mousedown', startDrawing);
      canvas.addEventListener('mouseup', stopDrawing);
      canvas.addEventListener('mousemove', draw);
      canvas.addEventListener('mouseleave', stopDrawing);

      toolSelector.addEventListener('change', (event) => {
        setCanvases((prevCanvases) => ({
          ...prevCanvases,
          [frameNumber]: {
            ...prevCanvases[frameNumber],
            tool: event.target.value,
          },
        }));
      });

      colorPicker.addEventListener('input', (event) => {
        setCanvases((prevCanvases) => ({
          ...prevCanvases,
          [frameNumber]: {
            ...prevCanvases[frameNumber],
            color: event.target.value,
          },
        }));
      });

      sizeSelector.addEventListener('change', (event) => {
        setCanvases((prevCanvases) => ({
          ...prevCanvases,
          [frameNumber]: {
            ...prevCanvases[frameNumber],
            size: event.target.value,
          },
        }));
      });

      // Update the state with the new canvases
      setCanvases(updatedCanvases);

      // Cleanup function
      return () => {
        canvas.removeEventListener('mousedown', startDrawing);
        canvas.removeEventListener('mouseup', stopDrawing);
        canvas.removeEventListener('mousemove', draw);
        canvas.removeEventListener('mouseleave', stopDrawing);
      };
    }, [frameNumber, canvases]);

  };

  const startDrawing = (event) => {
    const frameNumber = getFrameNumber(event.target);
    console.log(`startDrawing called for frame ${frameNumber}`);
    const { context, tool, color, size } = canvases[frameNumber];
    const { offsetX, offsetY } = event;

    console.log(`TOOL: ${tool}, COLOR: ${color}, SIZE: ${size}, CONTEXT: ${context}`);

    if (tool === 'pen') {
      context.beginPath();
      context.moveTo(offsetX, offsetY);
      context.strokeStyle = color;
      context.lineWidth = getSizeValue(size);
      setCanvases((prevCanvases) => ({
        ...prevCanvases,
        [frameNumber]: {
          ...prevCanvases[frameNumber],
          drawing: true,
        },
      }));
    } else if (tool === 'text') {
      const text = prompt('Enter text:');
      if (text) {
        context.font = `${getSizeValue(size) * 5}px Arial`;
        context.fillStyle = color;
        context.fillText(text, offsetX, offsetY);
      }
    }
  };

  const stopDrawing = (event) => {
    const frameNumber = getFrameNumber(event.target);
    console.log(`stopDrawing called for frame ${frameNumber}`);
    setCanvases((prevCanvases) => ({
      ...prevCanvases,
      [frameNumber]: {
        ...prevCanvases[frameNumber],
        drawing: false,
      },
    }));
  };

  const draw = (event) => {
    const frameNumber = getFrameNumber(event.target);
    const { context, drawing, tool, color, size } = canvases[frameNumber];
    const { offsetX, offsetY } = event;
    console.log(`TEST 1 ${drawing}`);
    if (!drawing) return;
    console.log(`TEST 2 ${drawing}`);
    if (tool === 'pen') {
      context.lineTo(offsetX, offsetY);
      context.stroke();
    }
  };

  const saveImage = (frameNumber) => {
    const canvas = canvases[frameNumber].canvas;
    const imageName = document.getElementById(`searchInput${frameNumber}`).value;

    canvas.toBlob((blob) => {
      const formData = new FormData();
      formData.append('image', blob, `${imageName}.png`);

      fetch('http://localhost:3001/upload', {
        method: 'POST',
        body: formData,
      })
        .catch((error) => {
          console.error(error);
        });
    }, 'image/png');
  };

  const getSizeValue = (size) => {
    switch (size) {
      case 'small':
        return 2;
      case 'medium':
        return 5;
      case 'large':
        return 10;
      default:
        return 5;
    }
  };

  const getFrameNumber = (target) => {
    const match = target.id.match(/\d+/);
    return match ? match[0] : null;
  };

  const handleChange = (event, frameNumber) => {
    const { id, value } = event.target;

    console.log(`handleChange called: ${id}, ${value}`);

    setCanvases((prevCanvases) => ({
      ...prevCanvases,
      [frameNumber]: {
        ...prevCanvases[frameNumber],
        searchInput: value,
      },
    }));
  };

  return (
    <div>
      <h1>Bildhantering</h1>

      <input type="file" id="fileInput" style={{ display: 'none' }} ref={fileInputRef} />
      <button onClick={openFileInput}>Ladda upp bild</button>

      <div className="image-frame" id="imageFrame1">
        <input
          type="text"
          placeholder="Sök efter bild"
          id={`searchInput${1}`}
          value={canvases[1]?.searchInput || ''}
          onChange={(e) => handleChange(e, 1)}
        />
        <button onClick={() => searchImage(1)}>Sök</button>
        <div className="canvas-container">
          <canvas className="image-canvas" id="imageCanvas1" ref={canvasRef}></canvas>
          <select id="toolSelector1" ref={toolSelectorRef}>
            <option value="pen">Pen</option>
            <option value="text">Text</option>
          </select>
          <label htmlFor="colorPicker1">Color:</label>
          <input type="color" id="colorPicker1" value="#000000" ref={colorPickerRef} />
          <label htmlFor="sizeSelector1">Size:</label>
          <select id="sizeSelector1" ref={sizeSelectorRef}>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
          <button className="save-button" onClick={() => saveImage(1)}>Save</button>
        </div>
      </div>

      <div className="image-frame" id="imageFrame2">
        <input
          type="text"
          placeholder="Sök efter bild"
          id={`searchInput${2}`}
          value={canvases[2]?.searchInput || ''}
          onChange={(e) => handleChange(e, 2)}
        />
        <button onClick={() => searchImage(2)}>Sök</button>
        <div className="canvas-container">
          <canvas className="image-canvas" id="imageCanvas2" ref={canvasRef}></canvas>
          <select id="toolSelector2" ref={toolSelectorRef}>
            <option value="pen">Pen</option>
            <option value="text">Text</option>
          </select>
          <label htmlFor="colorPicker2">Color:</label>
          <input type="color" id="colorPicker2" value="#000000" ref={colorPickerRef} />
          <label htmlFor="sizeSelector2">Size:</label>
          <select id="sizeSelector2" ref={sizeSelectorRef}>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
          <button className="save-button" onClick={() => saveImage(2)}>Save</button>
        </div>
      </div>
    </div>
  );
};

export default ImageEmployee;
