import React, { useState, useEffect, useRef } from 'react';

const ImageEmployee = () => {
  const [searchInput, setSearchQuery] = useState('');
  const [imageBlob, setImageBlob] = useState(null);
  const [canvasSize, setCanvasSize] = useState({ width: 0, height: 0 });

  // Refs for the canvas and its 2D context
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  useEffect(() => {
    // Cleanup function
    return () => {
      // Clear the imageBlob when the component is unmounted
      URL.revokeObjectURL(imageBlob);
    };
  }, [imageBlob]);

  useEffect(() => {
    if (imageBlob) {
      const img = new Image();
      img.onload = () => {
        console.log('Image dimensions:', img.width, img.height);

        // Ensure that the image dimensions are available
        if (img.width && img.height) {
          setCanvasSize({ width: img.width, height: img.height });

          const canvas = canvasRef.current;
          if (canvas) {
            canvas.width = img.width;
            canvas.height = img.height;

            const context = canvas.getContext('2d');
            if (context) {
              contextRef.current = context;
              contextRef.current.drawImage(img, 0, 0, img.width, img.height);
            } else {
              console.error('Could not get 2D context for canvas.');
            }
          } else {
            console.error('Canvas element not found.');
          }
        } else {
          console.error('Invalid image dimensions.');
        }
      };
      img.onerror = (error) => {
        console.error('Error loading image:', error);
      };
      img.src = URL.createObjectURL(imageBlob);
    }
  }, [imageBlob]);

  const searchImage = () => {
    // Perform the search logic here
    console.log('Searching for image:', searchInput);

    fetch(`http://localhost:3001/image/${searchInput}`)
      .then((response) => {
        if (response.ok) {
          return response.blob();
        } else {
          throw new Error('Image not found');
        }
      })
      .then((blob) => {
        setImageBlob(blob);
      })
      .catch((error) => {
        console.error('Something went wrong while fetching the image:', error);
      });
  };

  // Handle painting on the canvas
  const handleCanvasClick = (e) => {
    const x = e.nativeEvent.offsetX;
    const y = e.nativeEvent.offsetY;

    // Customize your painting logic here
    if (contextRef.current) {
      contextRef.current.fillStyle = 'red';
      contextRef.current.fillRect(x, y, 10, 10);
    }
  };

  return (
    <div>
      <h1>Bildhantering</h1>
      <input
        type="text"
        placeholder="SÃ¶k efter bild"
        value={searchInput}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <button onClick={searchImage}>Search</button>
      {canvasSize.width > 0 && canvasSize.height > 0 && (
        <canvas
          ref={canvasRef}
          width={canvasSize.width}
          height={canvasSize.height}
          style={{ border: '1px solid black' }}
          onClick={handleCanvasClick}
        ></canvas>
      )}
    </div>
  );
};

export default ImageEmployee;
